    private void btnMostrarActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnMostrarActionPerformed
        if (listarCuerposCelestes() == 0) {
            JOptionPane.showMessageDialog(rootPane, "No hay datos en el fichero.", "Información", JOptionPane.INFORMATION_MESSAGE);
        } else {
            listarCuerposCelestes();
            jDialogMostrar.setVisible(true);
            jDialogMostrar.setSize(473, 400);
            jDialogMostrar.setLocationRelativeTo(null);

        }

    }//GEN-LAST:event_btnMostrarActionPerformed

