    private void btnAceptarActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnAceptarActionPerformed
        if (anadirCuerpoCeleste()) {
            JOptionPane.showMessageDialog(rootPane, "Cuerpo Celeste añadido con éxito.", "Cuerpo añadido", JOptionPane.INFORMATION_MESSAGE);
            txtCodigo.setText("");
            txtCodigo.requestFocus();
            txtNombre.setText("");
            txtDiametro.setText("");
            cmbElegirTipo.setSelectedIndex(0);
        }
    }//GEN-LAST:event_btnAceptarActionPerformed

